//import React from 'react';
import './App.css';
import React, {useState} from 'react';
//import Todo from './Todo.js';
import ListItem from './Listitem.js';

function App() {

  const [taskList, setTaskList] = useState([]);
  const [current, setTask] = useState("");

  function addItem(e){
     e.preventDefault();
     const item={ task:current, key:Date.now()}
     setTaskList([...taskList, item]);

     addToStorage(item);

  }

  function addToStorage(item)
  {
    let tasks=localStorage.getItem("task_table");
     if(tasks==null)
     {
       work=[];
      }
     else{
         work=JSON.parse(tasks);
         }
     work.push({'Task':item.task});
     localStorage.setItem("task_table",JSON.stringify(work));
  }

    function handleChange(e){
      if(e.target.value!=="")
      { 
        setTask(e.target.value);
      }
    }

  return (
    <div className="App">
          <h2 id="todo-header">TODO LIST</h2>
          <form id="todo-form" onSubmit={addItem}>
            <input type="text" onChange={handleChange} name="theTask"
                   placeholder="Enter your task here"/>
            <button type="submit">Add Task</button>
          </form>
          <ListItem items={taskList}/>
    </div>
  );

}

export default App;
