import React from 'react';
//import './Listitem.css'

function ListItem(props)
{
    const items=props.task;
    const listItems= items.map(item =>
        {
            return <div className="list" key={item.key}>
                     <table>
                       <tbody>
                            <tr>
                                <td style={{width:"180px",padding:"10px"}}>{item.theTask}</td>
                                <td style={{width:"30px"}}><button id="edit"
                                        onClick={()=>props.editItem(item.text,item.key)}>Edit</button>
                                </td>
                                <td style={{width:"30px"}}><button id="delete"
                                        onClick={()=>props.deleteItem(item.key)}>Delete</button>
                                </td>
                            </tr>
                       </tbody>
                     </table>
                   </div>
        })

    return <div>{listItems}</div>
}


export default ListItem
