//import React from 'react';
import './App.css';
import React, {useState , useRef , useEffect} from 'react';
import ListItem from './Listitem.js';

function App() {

  const [taskList, setTaskList] = useState([]);
  const [current, setTask] = useState("");

  const inputRef=useRef(null);
  useEffect(()=>{
    inputRef.current.focus();
  });

  function addItem(e){
     e.preventDefault();
     const item={ task:current, key:Date.now()}
     setTaskList([...taskList, item]);

     inputRef.current.value=" ";

     addToStorage(item);

  }

  function addToStorage(item)
  {
    let tasks=localStorage.getItem("task_table");
     if(tasks==null)
     {
       var work=[];
      }
     else{
          work=JSON.parse(tasks);
         }
     work.push({'Task':item.task,'Key':item.key});
     localStorage.setItem("task_table",JSON.stringify(work));

  }

    function handleChange(e){
      if(e.target.value!=="")
      { 
        setTask(e.target.value);
      }
    }

    function deleteItem(key)
    {
      const filteredItems= taskList.filter(item => item.key!==key);
      setTaskList(filteredItems);
      deleteFromStorage(key);
    }

    function deleteFromStorage(key)
    {
          let m=[];
        m=getInfo();

        m= m.filter(function(m,index) {
            return m.Key!==key
          });

          localStorage.setItem('task_table',JSON.stringify(m));
    }

    function getInfo() {
      let data; 

      if(localStorage.getItem('task_table')===null){
          data=[];
      } else{
          data= JSON.parse(localStorage.getItem('pro_table'));

      }
      return data;
    }

  return (
    <div className="App">
          <h2 id="todo-header">TODO LIST</h2>
          <form id="todo-form" onSubmit={addItem} autoComplete="off">
            <input type="text" onChange={handleChange} name="theTask"
                   placeholder="Enter your task here" ref={inputRef}/>
            <button type="submit">Add Task</button>
          </form>
          <ListItem items={taskList} 
                    deleteItem={deleteItem}/>
    </div>
  );

}

export default App;
